import React, { Component } from 'react';
import {Link} from 'react-router-dom';

/* STYLE */
import "../style/_header.scss";

/* PAGE */
import Home from '../pages/home';
import Projects from '../pages/projects';
import Studies from '../pages/studies';

/* IMG */
import logo from '../img/logo_42.jpeg';



class Header extends Component {
    state = { log: false };

    handleClick = (e) => {
        e.preventDefault();
        if (state.log = true)
            this.setState({log: false});
        else
            this.setState({log: true})

        console.log(this.state.log)
    }

    is_logged = (state) => {
        if (state.log == true)
            return (
                <div>
                    <div className="navigation__button--logout" onClick={this.handleClick}>Deconnexion</div>
                </div>
            );

        else
            return (
                <div className="navigation__buttons">
                    <div className="navigation__buttons--login" onClick={this.handleClick}>Connexion</div>
                    <div className="navigation__button--signin">Inscription</div>
                </div>
            );


    }
    render() { 
        return (
        <div className="header">
            <img src={logo} alt="42 logo" className="header--logo"></img>         
            <div className="header__navigation">
                <div className="header__navigation--logo">LOGO</div>
                <div className="navigation__rubric">
                    <div className="navigation__rubric--page"></div>
                    <div className="navigation__rubric--page"></div>
                    <div className="navigation__rubric--page"></div>
                    <div className="navigation__rubric--page"></div>
                </div>
                <div className="navigation__buttons">
                    {this.is_logged(this.state.log)}
                </div>

            </div>
        </div>
        )
    }
}

export default Header;
